import{_ as t,c as o,o as a,a2 as i}from"./chunks/framework.B8WFj13S.js";const r="/documentation/pr-preview/pr-2/assets/cookie-00.D0lxgtav.jpg",n="/documentation/pr-preview/pr-2/assets/cookie.DIt5qGrU.jpg",s="/documentation/pr-preview/pr-2/assets/cookie-02.By159sQe.jpg",l="/documentation/pr-preview/pr-2/assets/cookie-03.3mKUZgzD.jpg",h="/documentation/pr-preview/pr-2/assets/cookie-05.BakmJwHT.jpg",f=JSON.parse('{"title":"Gardener Cookies","description":"","frontmatter":{"github_repo":"https://github.com/gardener/documentation","github_subdir":"website/blog/2018","params":{"github_branch":"master"},"path_base_for_github_subdir":{"from":"content/blog/2018/12.25-Gardener_Cookies.md","to":"12.25-Gardener_Cookies.md"},"publishdate":"2018-12-25","title":"Gardener Cookies"},"headers":[],"relativePath":"blog/2018/12.25-Gardener_Cookies/index.md","filePath":"blog/2018/12.25-Gardener_Cookies.md","lastUpdated":null}'),d={name:"blog/2018/12.25-Gardener_Cookies/index.md"};function c(u,e,g,p,m,b){return a(),o("div",null,e[0]||(e[0]=[i('<h1 id="green-tea-matcha-cookies" tabindex="-1">Green Tea Matcha Cookies <a class="header-anchor" href="#green-tea-matcha-cookies" aria-label="Permalink to &quot;Green Tea Matcha Cookies&quot;">â€‹</a></h1><p>For a team event during the Christmas season we decided to completely reinterpret the topic <code>cookies</code>. ðŸ˜ƒ</p><img style="width:50%;height:auto;margin:0, auto;" src="'+r+'"><p>Matcha cookies have the delicate flavor and color of green tea. These soft, pillowy and chewy green tea cookies are perfect with tea. And of course they fit perfectly to our logo.</p><h2 id="ingredients" tabindex="-1">Ingredients <a class="header-anchor" href="#ingredients" aria-label="Permalink to &quot;Ingredients&quot;">â€‹</a></h2><ul><li>1 stick butter, softened</li><li>â…ž cup of granulated sugar</li><li>1 cup + 2 tablespoons all-purpose flour</li><li>2 eggs</li><li>1Â¼ tablespoons culinary grade matcha powder</li><li>1 teaspoon baking powder</li><li>pinch of salt</li></ul><h2 id="instructions" tabindex="-1">Instructions <a class="header-anchor" href="#instructions" aria-label="Permalink to &quot;Instructions&quot;">â€‹</a></h2><ol><li>Cream together the butter and sugar in a large mixing bowl - it should be creamy colored and airy. A hand blender or stand mixer works well for this. This helps the cookie become fluffy and chewy.</li><li>Gently incorporate the eggs to the butter mixture one at a time.</li><li>In a separate bowl, sift together all the dry ingredients.</li><li>Add the dry ingredients to the wet by adding a little at a time and folding or gently mixing the batter together. Keep going until you&#39;ve incorporated all the remaining flour mixture. The dough should be a beautiful green color.</li><li>Chill the dough for at least an hour - up to overnight. The longer the better!</li><li>Preheat your oven to 325 F.</li><li>Roll the dough into balls the size of ping pong balls and place them on a non-stick cookie sheet.</li><li>Bake them for 12-15 minutes until the bottoms just start to become golden brown and the cookie no longer looks wet in the middle. Note: you can always bake them at 350 F for a less moist, fluffy cookie. It will bake faster by about 2-4 minutes 350 F so watch them closely.</li><li>Remove and let cool on a rack and enjoy!</li></ol><h2 id="note" tabindex="-1">Note <a class="header-anchor" href="#note" aria-label="Permalink to &quot;Note&quot;">â€‹</a></h2><p>Make sure you get culinary grade matcha powder. You should be able to find this in Asian or natural grocers.</p><img style="width:50%;height:auto;" src="'+n+'"><img style="width:50%;height:auto;" src="'+s+'"><img style="width:50%;height:auto;" src="'+l+'"><img style="width:50%;height:auto;" src="'+h+'">',14)]))}const k=t(d,[["render",c]]);export{f as __pageData,k as default};
