import{_ as n,C as r,c as a,o,a2 as i,G as s}from"./chunks/framework.B8WFj13S.js";const f=JSON.parse('{"title":"Community Call - Gardener Extension Development","description":"","frontmatter":{"aliases":["/blog/2022/06/17/01"],"authors":[{"avatar":"https://avatars.githubusercontent.com/u/25197046?s=400&u=56175926393a77892662001f0dca5a439d1e771f&v=4","email":"nikolay.boshnakov@sap.com","name":"Nikolay Boshnakov"}],"github_repo":"https://github.com/gardener/documentation","github_subdir":"website/blog/2022","linkTitle":"Community Call - Gardener Extension Development","newsSubtitle":"June 17, 2022","params":{"github_branch":"master"},"path_base_for_github_subdir":{"from":"content/blog/2022/06.17-Gardener-Community-Meeting-June.md","to":"06.17-Gardener-Community-Meeting-June.md"},"publishdate":"2022-06-17","title":"Community Call - Gardener Extension Development"},"headers":[],"relativePath":"blog/2022/06.17-Gardener-Community-Meeting-June/index.md","filePath":"blog/2022/06.17-Gardener-Community-Meeting-June.md","lastUpdated":null}'),l={name:"blog/2022/06.17-Gardener-Community-Meeting-June/index.md"};function d(h,e,u,m,c,g){const t=r("YouTubeVideo");return o(),a("div",null,[e[0]||(e[0]=i('<h1 id="community-call-gardener-extension-development" tabindex="-1">Community Call - Gardener Extension Development <a class="header-anchor" href="#community-call-gardener-extension-development" aria-label="Permalink to &quot;Community Call - Gardener Extension Development&quot;">​</a></h1><h2 id="presenters" tabindex="-1">Presenters <a class="header-anchor" href="#presenters" aria-label="Permalink to &quot;Presenters&quot;">​</a></h2><p>This community call was led by <a href="https://github.com/jensac" target="_blank" rel="noreferrer">Jens Schneider</a> and Lothar Gesslein.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Starting the development of a new Gardener extension can be challenging, when you are not an expert in the Gardener ecosystem yet. Therefore, the first half of this community call led by Jens Schneider aims to provide a &quot;getting started tutorial&quot; at a beginner level. <a href="https://23technologies.cloud/en" target="_blank" rel="noreferrer">23Technologies</a> have developed a minimal working example for Gardener extensions, <a href="https://github.com/23technologies/gardener-extension-mwe" target="_blank" rel="noreferrer">gardener-extension-mwe</a>, hosted in a Github repository. Jens is following the <a href="https://23technologies.cloud/en/blog/gardener-ext-dev" target="_blank" rel="noreferrer">Getting started with Gardener extension development</a> tutorial, which aims to provide exactly that.</p><p>In the second part of the community call, Lothar Gesslein introduces the <a href="https://github.com/23technologies/gardener-extension-shoot-flux" target="_blank" rel="noreferrer">gardener-extension-shoot-flux</a>, which allows for the automated installation of arbitrary Kubernetes resources into shoot clusters. As this extension relies on <a href="https://fluxcd.io/" target="_blank" rel="noreferrer">Flux</a>, an overview of Flux&#39;s capabilities is also provided.</p><p>If you are left with any questions regarding the content, you might find the answers at the Q&amp;A session and discussion held at the end.</p><p>You can find the tutorials in this community call at:</p><ul><li><a href="https://23technologies.cloud/en/blog/gardener-ext-dev" target="_blank" rel="noreferrer">Getting started with Gardener extension development</a></li><li><a href="https://23technologies.cloud/en/blog/gardener-ext-shoot-flux" target="_blank" rel="noreferrer">A Gardener Extension for universal Shoot Configuration</a></li></ul><p>If you are left with any questions regarding the content, you might find the answers at the Q&amp;A session and discussion held at the end of the meeting.</p><h2 id="recording" tabindex="-1">Recording <a class="header-anchor" href="#recording" aria-label="Permalink to &quot;Recording&quot;">​</a></h2>',11)),s(t,{videoId:"nG2FRYL05mc"})])}const b=n(l,[["render",d]]);export{f as __pageData,b as default};
