import{_ as t,c as a,o as i,a2 as n}from"./chunks/framework.Bfq10Vlj.js";const p=JSON.parse('{"title":"Style Guide","description":"","frontmatter":{"Title":"Style Guide","github_repo":"https://github.com/gardener/documentation","github_subdir":"website/documentation/contribute/documentation/style-guide","params":{"github_branch":"master"},"path_base_for_github_subdir":{"from":"content/docs/contribute/documentation/style-guide/_index.md","to":"_index.md"},"title":"Style Guide","prev":false,"next":false},"headers":[],"relativePath":"docs/contribute/documentation/style-guide/_index.md","filePath":"docs/contribute/documentation/style-guide/_index.md","lastUpdated":null}'),o={name:"docs/contribute/documentation/style-guide/_index.md"};function r(s,e,l,d,c,u){return i(),a("div",null,e[0]||(e[0]=[n(`<p>This page gives writing style guidelines for the Gardener documentation. For formatting guidelines, see the <a href="/docs/contribute/documentation/formatting-guide/">Formatting Guide</a>.</p><p>These are guidelines, not rules. Use your best judgment, and feel free to propose changes to this document in a Pull Request.</p><ul><li><a href="/docs/contribute/documentation/style-guide/#structure">Structure</a><ul><li><a href="/docs/contribute/documentation/style-guide/#documentation-types-overview">Documentation Types Overview</a></li><li><a href="/docs/contribute/documentation/style-guide/#structure-and-file-names">Structure and File Names</a></li><li><a href="/docs/contribute/documentation/style-guide/#topic-structure">Topic Structure</a></li><li><a href="/docs/contribute/documentation/style-guide/#front-matter">Front Matter</a></li><li><a href="/docs/contribute/documentation/style-guide/#alerts">Alerts</a></li><li><a href="/docs/contribute/documentation/style-guide/#images">Images</a></li><li><a href="/docs/contribute/documentation/style-guide/#general-tips">General Tips</a></li></ul></li><li><a href="/docs/contribute/documentation/style-guide/#language-and-grammar">Language and Grammar</a><ul><li><a href="/docs/contribute/documentation/style-guide/#language">Language</a></li><li><a href="/docs/contribute/documentation/style-guide/#writing-style">Writing Style</a></li><li><a href="/docs/contribute/documentation/style-guide/#creating-titles-and-headers">Creating Titles and Headers</a></li></ul></li><li><a href="/docs/contribute/documentation/style-guide/#related-links">Related Links</a></li></ul><h2 id="structure" tabindex="-1">Structure <a class="header-anchor" href="#structure" aria-label="Permalink to &quot;Structure&quot;">​</a></h2><h3 id="documentation-types-overview" tabindex="-1">Documentation Types Overview <a class="header-anchor" href="#documentation-types-overview" aria-label="Permalink to &quot;Documentation Types Overview&quot;">​</a></h3><p>The following table summarizes the types of documentation and their mapping to the UA taxonomy. Every topic you create will fall into one of these categories.</p><table tabindex="0"><thead><tr><th style="text-align:left;">Gardener Content Type</th><th style="text-align:left;">Definition</th><th style="text-align:left;">Example</th><th style="text-align:left;">Content</th><th style="text-align:left;">Comparable UA Content Type</th></tr></thead><tbody><tr><td style="text-align:left;">Concept</td><td style="text-align:left;">Introduce a functionality or concept; covers background information.</td><td style="text-align:left;"><a href="https://kubernetes.io/docs/concepts/services-networking/service/" target="_blank" rel="noreferrer">Services</a></td><td style="text-align:left;"><a href="/docs/contribute/documentation/style-guide/concept_template/">Overview, Relevant headings</a></td><td style="text-align:left;">Concept</td></tr><tr><td style="text-align:left;">Reference</td><td style="text-align:left;">Provide a reference, for example, list all command line options of <code>gardenctl</code> and what they are used for.</td><td style="text-align:left;"><a href="https://kubernetes.io/docs/reference/kubectl/overview/" target="_blank" rel="noreferrer">Overview of kubectl</a></td><td style="text-align:left;"><a href="/docs/contribute/documentation/style-guide/reference_template/">Relevant headings</a></td><td style="text-align:left;">Reference</td></tr><tr><td style="text-align:left;">Task</td><td style="text-align:left;">A step-by-step description that allows users to complete a specific task.</td><td style="text-align:left;"><a href="https://kubernetes.io/docs/tasks/administer-cluster/kubeadm/kubeadm-upgrade/" target="_blank" rel="noreferrer">Upgrading kubeadm clusters</a></td><td style="text-align:left;"><a href="/docs/contribute/documentation/style-guide/task_template/">Overview, Prerequisites, Steps, Result</a></td><td style="text-align:left;">Complex Task</td></tr><tr><td style="text-align:left;">Trail</td><td style="text-align:left;">Collection of all other content types to cover a big topic.</td><td style="text-align:left;"><a href="https://docs.oracle.com/javase/tutorial/networking/TOC.html" target="_blank" rel="noreferrer">Custom Networking</a></td><td style="text-align:left;">None</td><td style="text-align:left;">Maps</td></tr><tr><td style="text-align:left;">Tutorial</td><td style="text-align:left;">A combination of many tasks that allows users to complete an example task with the goal to learn the details of a given feature.</td><td style="text-align:left;"><a href="https://kubernetes.io/docs/tutorials/stateful-application/cassandra/" target="_blank" rel="noreferrer">Deploying Cassandra with a StatefulSet</a></td><td style="text-align:left;">Overview, Prerequisites, Tasks, Result</td><td style="text-align:left;">Tutorial</td></tr></tbody></table><p>See the <a href="/docs/contribute/">Contributors Guide</a> for more details on how to produce and contribute documentation.</p><h3 id="structure-and-file-names" tabindex="-1">Structure and File Names <a class="header-anchor" href="#structure-and-file-names" aria-label="Permalink to &quot;Structure and File Names&quot;">​</a></h3><p>When creating a new documentation topic or folder, it is important to adhere to a consistent and clear naming convention. This ensures that topics are easily identifiable and helps maintain a well-structured documentation repository. The title of your newly created content should be in dash-case (also known as kebab-case). This means each word in the file or folder name should be separated by a hyphen (-), and all letters should be in lowercase.</p><p>By default, this will also be the title of your topic in the website.</p><p>For example, a <code>new-topic-title.md</code> filename will generate the topic&#39;s title as &quot;New Topic Title&quot;. You can customize the title by adding <a href="/docs/contribute/documentation/style-guide/#front-matter">front matter</a> to the top of your Markdown file.</p><p>If you would like to have a newly added folder appear in the website navigation, you need to add an <code>_index.md</code> file to it. As folders are hidden by default, this file will hold its title, description, weight, and other possible properties. For more information, see the <a href="/docs/contribute/documentation/style-guide/#front-matter">Front Matter</a> section.</p><h3 id="topic-structure" tabindex="-1">Topic Structure <a class="header-anchor" href="#topic-structure" aria-label="Permalink to &quot;Topic Structure&quot;">​</a></h3><p>When creating a topic, you will need to follow a certain structure. A topic generally comprises of, in order:</p><ul><li><a href="/docs/contribute/documentation/style-guide/#front-matter">Metadata</a> (Specific for <code>.md</code> files in Gardener) - Additional information about the topic</li><li>Title - A short, descriptive name for the topic</li><li>Content - The main part of the topic. It contains all the information relevant to the user <ul><li>Concept content: <a href="/docs/contribute/documentation/style-guide/concept_template/">Overview, Relevant headings</a></li><li>Task content: <a href="/docs/contribute/documentation/style-guide/task_template/">Overview, Prerequisites, Steps, Result</a></li><li>Reference content: <a href="/docs/contribute/documentation/style-guide/reference_template/">Relevant headings</a></li></ul></li><li>Related Links (Optional) - A part after the main content that contains links that are not a part of the topic, but are still connected to it</li></ul><p>You can use the provided content description files as a template for your own topics.</p><h3 id="front-matter" tabindex="-1">Front Matter <a class="header-anchor" href="#front-matter" aria-label="Permalink to &quot;Front Matter&quot;">​</a></h3><p><a href="https://gohugo.io/content-management/front-matter" target="_blank" rel="noreferrer">Front matter</a> is metadata applied at the head of each content Markdown file. It is used to instruct the static site generator build process. The format is YAML and it must be enclosed in leading and trailing comment dashes (<code>---</code>).</p><p>Sample codeblock:</p><div class="language-yaml vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">---</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">title</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">Getting Started</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">description</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">Guides to get you accustomed with Gardener</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">weight</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">10</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">aliases</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: [</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;/docs/old-getting-started-url&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">]</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">---</span></span>
<span class="line"></span>
<span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&lt;topic-content&gt;</span></span></code></pre></div><p>There are a number of <a href="https://gohugo.io/content-management/front-matter#predefined" target="_blank" rel="noreferrer">predefined</a> front matter properties, but not all of them are considered by the layouts developed for the website. The most essential ones to consider are:</p><ul><li><p><code>title</code> the content title that will be used as page title and in navigation structures. If not set, the title of the topic will be file name in uppercase (e.g., <code>new-topic-title.md</code> will be rendered as &quot;New Topic Title&quot;).</p></li><li><p><code>description</code> describes the content. For some content types such as documentation guides, it may be rendered in the UI.</p></li><li><p><code>weight</code> a positive integer number that controls the ordering of the content in navigation structures. The lower it is, the higher the topic will be on the page. Topics with no set weight are sorted in alphabetical order, with any weighted topics appearing above them.</p></li><li><p><code>url</code> if specified, it will override the default url constructed from the file path to the content. Make sure the url you specify is consistent and meaningful. Prefer short paths. Do not provide redundant URLs!</p></li><li><p><code>persona</code> specifies the type of user the topic is aimed towards. Use only a single persona per topic.</p><div class="language-yaml vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">persona</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">Users / Operators / Developers</span></span></code></pre></div></li><li><p><code>aliases</code> is an array of strings that contain the old paths to the topic. Useful in case you need to rename or change the location of a topic, but want to keep any existing bookmarks.</p></li></ul><p>While this section will be automatically generated if your topic has a title header, adding more detailed information helps other users, developers, and technical writers better sort, classify and understand the topic.</p><p>By using a metadata section you can also skip adding a title header or overwrite it in the navigation section.</p><h3 id="alerts" tabindex="-1">Alerts <a class="header-anchor" href="#alerts" aria-label="Permalink to &quot;Alerts&quot;">​</a></h3><p>If you want to add a note, tip or a warning to your topic, use the templates provides in the <a href="/docs/contribute/documentation/shortcodes/#alert">Shortcodes</a> documentation.</p><h3 id="images" tabindex="-1">Images <a class="header-anchor" href="#images" aria-label="Permalink to &quot;Images&quot;">​</a></h3><p>If you want to add an image to your topic, it is recommended to follow the guidelines outlined in the <a href="/docs/contribute/documentation/images/">Images</a> documentation.</p><h3 id="general-tips" tabindex="-1">General Tips <a class="header-anchor" href="#general-tips" aria-label="Permalink to &quot;General Tips&quot;">​</a></h3><ul><li>Try to create a succinct title and an informative description for your topics</li><li>If a topic feels too long, it might be better to split it into a few different ones</li><li>Avoid having have more than ten steps in one a task topic</li><li>When writing a tutorial, link the tasks used in it instead of copying their content</li></ul><h2 id="language-and-grammar" tabindex="-1">Language and Grammar <a class="header-anchor" href="#language-and-grammar" aria-label="Permalink to &quot;Language and Grammar&quot;">​</a></h2><h3 id="language" tabindex="-1">Language <a class="header-anchor" href="#language" aria-label="Permalink to &quot;Language&quot;">​</a></h3><ul><li>Gardener documentation uses US English</li><li>Keep it simple and use words that non-native English speakers are also familiar with</li><li>Use the <a href="https://www.merriam-webster.com/" target="_blank" rel="noreferrer">Merriam-Webster Dictionary</a> when checking the spelling of words</li></ul><h3 id="writing-style" tabindex="-1">Writing Style <a class="header-anchor" href="#writing-style" aria-label="Permalink to &quot;Writing Style&quot;">​</a></h3><ul><li>Write in a conversational manner and use simple present tense</li><li>Be friendly and refer to the person reading your content as &quot;you&quot;, instead of standard terms such as &quot;user&quot;</li><li>Use an active voice - make it clear who is performing the action</li></ul><h3 id="creating-titles-and-headers" tabindex="-1">Creating Titles and Headers <a class="header-anchor" href="#creating-titles-and-headers" aria-label="Permalink to &quot;Creating Titles and Headers&quot;">​</a></h3><ul><li>Use <a href="https://titlecaseconverter.com/words-to-capitalize/" target="_blank" rel="noreferrer">title case</a> when creating titles or headers</li><li>Avoid adding additional formatting to the title or header</li><li>Concept and reference topic titles should be simple and succinct</li><li>Task and tutorial topic titles begin with a verb</li></ul><h2 id="related-links" tabindex="-1">Related Links <a class="header-anchor" href="#related-links" aria-label="Permalink to &quot;Related Links&quot;">​</a></h2><ul><li><a href="/docs/contribute/documentation/formatting-guide/">Formatting Guide</a></li><li><a href="/docs/contribute/">Contributors Guide</a></li><li><a href="/docs/contribute/documentation/shortcodes/">Shortcodes</a></li><li><a href="/docs/contribute/documentation/images/">Images</a></li></ul>`,40)]))}const g=t(o,[["render",r]]);export{p as __pageData,g as default};
