import{_ as t,c as s,o,a2 as r}from"./chunks/framework.Bfq10Vlj.js";const i="/assets/new-gardener-project.KVUVXwXt.png",a="data:image/png;base64,",l="/assets/alicloud-create-secret.CivDwOW-.png",c="data:image/png;base64,",n="data:image/png;base64,",u="/assets/alicloud-copy-policy.Dvnp07yY.png",A="/assets/alicloud-create-policy.gzkiQOzC.png",d="/assets/alicloud-paste-policy.8i6ng7I5.png",p="/assets/alicloud-create-user.DOPspzhb.png",h="/assets/alicloud-input-user.BJkYKZmi.png",m="/assets/alicloud-user-created.DCvnPd1v.png",g="/assets/alicloud-grant-permission.DmBeUerB.png",b="/assets/alicloud-assign-policy._zbulWIs.png",C="/assets/alicloud-create-secret-1.BdLfZ7Qd.png",w="/assets/alicloud-create-secret-2.jUhSMdiV.png",_="/assets/new-cluster.OwmF3hKS.png",f="/assets/alicloud-create-cluster.1oLF3T6a.png",B="/assets/alicloud-processing-cluster.ByXSSpBi.png",y="/assets/alicloud-kubeconfig.l_pHFnLa.png",U=JSON.parse('{"title":"Create a Kubernetes Cluster on Alibaba Cloud with Gardener","description":"","frontmatter":{"category":"Getting Started","github_repo":"https://github.com/gardener/gardener-extension-provider-alicloud","github_subdir":"docs/tutorials/kubernetes-cluster-on-alicloud-with-gardener","level":"beginner","params":{"github_branch":"master"},"path_base_for_github_subdir":{"from":"content/docs/extensions/infrastructure-extensions/gardener-extension-provider-alicloud/tutorials/kubernetes-cluster-on-alicloud-with-gardener/kubernetes-cluster-on-alicloud-with-gardener.md","to":"kubernetes-cluster-on-alicloud-with-gardener.md"},"scope":"app-developer","title":"Create a Kubernetes Cluster on Alibaba Cloud with Gardener","prev":false,"next":false},"headers":[],"relativePath":"docs/extensions/infrastructure-extensions/gardener-extension-provider-alicloud/tutorials/kubernetes-cluster-on-alicloud-with-gardener/kubernetes-cluster-on-alicloud-with-gardener/index.md","filePath":"docs/extensions/infrastructure-extensions/gardener-extension-provider-alicloud/tutorials/kubernetes-cluster-on-alicloud-with-gardener/kubernetes-cluster-on-alicloud-with-gardener.md","lastUpdated":null}'),D={name:"docs/extensions/infrastructure-extensions/gardener-extension-provider-alicloud/tutorials/kubernetes-cluster-on-alicloud-with-gardener/kubernetes-cluster-on-alicloud-with-gardener/index.md"};function k(S,e,x,R,G,Y){return o(),s("div",null,e[0]||(e[0]=[r('<h1 id="create-a-kubernetes-cluster-on-alibaba-cloud-with-gardener" tabindex="-1">Create a Kubernetes Cluster on Alibaba Cloud with Gardener <a class="header-anchor" href="#create-a-kubernetes-cluster-on-alibaba-cloud-with-gardener" aria-label="Permalink to &quot;Create a Kubernetes Cluster on Alibaba Cloud with Gardener&quot;">​</a></h1><h3 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h3><p>Gardener allows you to create a Kubernetes cluster on different infrastructure providers. This tutorial will guide you through the process of creating a cluster on Alibaba Cloud.</p><h3 id="prerequisites" tabindex="-1">Prerequisites <a class="header-anchor" href="#prerequisites" aria-label="Permalink to &quot;Prerequisites&quot;">​</a></h3><ul><li>You have created an <a href="https://www.alibabacloud.com" target="_blank" rel="noreferrer">Alibaba Cloud account</a>.</li><li>You have access to the Gardener dashboard and have permissions to create projects.</li></ul><h3 id="steps" tabindex="-1">Steps <a class="header-anchor" href="#steps" aria-label="Permalink to &quot;Steps&quot;">​</a></h3><ol><li><p>Go to the Gardener dashboard and create a project.</p><img src="'+i+'"><blockquote><p>To be able to add shoot clusters to this project, you must first create a technical user on Alibaba Cloud with sufficient permissions.</p></blockquote></li><li><p>Choose <em>Secrets</em>, then the plus icon <img src="'+a+'"> and select <em>AliCloud</em>.</p><img src="'+l+'"></li><li><p>To copy the policy for Alibaba Cloud from the Gardener dashboard, click on the help icon <img src="'+c+'"> for Alibaba Cloud secrets, and choose copy <img src="'+n+'">.</p><img src="'+u+'"></li><li><p>Create a custom policy in Alibaba Cloud:</p><ol><li><p>Log on to your Alibaba account and choose <em>RAM</em> &gt; <em>Permissions</em> &gt; <em>Policies</em>.</p><img src="'+A+'"></li><li><p>Enter the name of your policy.</p></li><li><p>Select <code>Script</code>.</p></li><li><p>Paste the policy that you copied from the Gardener dashboard to this custom policy.</p></li><li><p>Choose <em>OK</em>.</p><img src="'+d+'"></li></ol></li><li><p>In the Alibaba Cloud console, create a new technical user:</p><ol><li><p>Choose <em>RAM</em> &gt; <em>Users</em>.</p></li><li><p>Choose <em>Create User</em>.</p><img src="'+p+'"></li><li><p>Enter a logon and display name for your user.</p></li><li><p>Select <em>Open API Access</em>.</p></li><li><p>Choose <em>OK</em>.</p><img src="'+h+'"></li></ol><blockquote><p>After the user is created, <code>AccessKeyId</code> and <code>AccessKeySecret</code> are generated and displayed. Remember to save them. The <code>AccessKey</code> is used later to create secrets for Gardener.</p></blockquote><img src="'+m+'"></li><li><p>Assign the policy you created to the technical user:</p><ol><li><p>Choose <em>RAM</em> &gt; <em>Permissions</em> &gt; <em>Grants</em>.</p></li><li><p>Choose <em>Grant Permission</em>.</p><img src="'+g+'"></li><li><p>Select <em>Alibaba Cloud Account</em>.</p></li><li><p>Assign the policy you’ve created before to the technical user.</p><img src="'+b+'"></li></ol></li><li><p>Create your secret.</p><ol><li>Type the name of your secret.</li><li>Copy and paste the <code>Access Key ID</code> and <code>Secret Access Key</code> you saved when you created the technical user on Alibaba Cloud.</li><li>Choose <em>Add secret</em>. <img src="'+C+'"></li></ol><blockquote><p>After completing these steps, you should see your newly created secret in the <em>Infrastructure Secrets</em> section.</p></blockquote><img src="'+w+'"></li><li><p>To create a new cluster, choose <em>Clusters</em> and then the plus sign in the upper right corner.</p><img src="'+_+'"></li><li><p>In the <em>Create Cluster</em> section:</p><ol><li><p>Select <em>AliCloud</em> in the <em>Infrastructure</em> tab.</p></li><li><p>Type the name of your cluster in the <em>Cluster Details</em> tab.</p></li><li><p>Choose the secret you created before in the <em>Infrastructure Details</em> tab.</p></li><li><p>Choose <em>Create</em>.</p><img src="'+f+'"></li></ol></li><li><p>Wait for your cluster to get created.</p><img src="'+B+'"></li></ol><h2 id="result" tabindex="-1">Result <a class="header-anchor" href="#result" aria-label="Permalink to &quot;Result&quot;">​</a></h2><p>After completing the steps in this tutorial, you will be able to see and download the kubeconfig of your cluster. With it you can create shoot clusters on Alibaba Cloud. <img src="'+y+'"></p><blockquote><p>The size of persistent volumes in your shoot cluster must at least be 20 GiB large. If you choose smaller sizes in your Kubernetes PV definition, the allocation of cloud disk space on Alibaba Cloud fails.</p></blockquote>',10)]))}const v=t(D,[["render",k]]);export{U as __pageData,v as default};
