import{_ as o,c as t,o as r,a2 as a}from"./chunks/framework.B8WFj13S.js";const u=JSON.parse('{"title":"Shoot Workload Identity","description":"Configure access to infrastructure accounts via workload identity instead of static credentials","frontmatter":{"aliases":["/docs/gardener/shoot-workload-identity/"],"description":"Configure access to infrastructure accounts via workload identity instead of static credentials","github_repo":"https://github.com/gardener/gardener","github_subdir":"docs/usage/shoot","params":{"github_branch":"master"},"path_base_for_github_subdir":{"from":"content/docs/gardener/shoot/shoot-workload-identity.md","to":"shoot-workload-identity.md"},"persona":"Users","title":"Shoot Workload Identity","prev":false,"next":false},"headers":[],"relativePath":"docs/gardener/shoot/shoot-workload-identity/index.md","filePath":"docs/gardener/shoot/shoot-workload-identity.md","lastUpdated":null}'),d={name:"docs/gardener/shoot/shoot-workload-identity/index.md"};function i(n,e,s,c,l,p){return r(),t("div",null,e[0]||(e[0]=[a('<h1 id="shoot-workload-identity" tabindex="-1">Shoot Workload Identity <a class="header-anchor" href="#shoot-workload-identity" aria-label="Permalink to &quot;Shoot Workload Identity&quot;">â€‹</a></h1><p><a href="/documentation/pr-preview/pr-2/docs/gardener/api-reference/security/#workloadidentity"><code>WorkloadIdentity</code></a> is a resource that allows workloads to be presented before external systems by giving them identities managed by Gardener. As <code>WorkloadIdentity</code>s do not directly contain credentials we gain the ability to create <code>Shoot</code>s without the need of preliminary exchange of credentials. For that to work users should establish trust to the Gardener Workload Identity Issuer in advance. The issuer URL can be read from the <a href="/documentation/pr-preview/pr-2/docs/gardener/gardener/gardener_info_configmap/">Gardener Info ConfigMap</a>.</p><div class="tip custom-block github-alert"><p class="custom-block-title">TIP</p><p><code>Shoot</code>s that were previously using <code>Secret</code>s as authentication method can also be migrated to use <code>WorkloadIdentity</code>. As the <code>credentialsRef</code> field of <a href="/documentation/pr-preview/pr-2/docs/gardener/api-reference/security/#credentialsbinding"><code>CredentialsBinding</code></a> is immutable, one would have to create a new <code>CredentialsBinding</code> that references a <code>WorkloadIdentity</code> and set the <code>.spec.credentialsBindingName</code> field of the <code>Shoot</code> to refer to the newly created <code>CredentialsBinding</code>.</p></div><p>As of now <code>WorkloadIdentity</code> is supported for AWS, Azure and GCP. For detailed explanation on how to enable the feature, please consult the provider extension specific documentation:</p><ul><li><a href="/documentation/pr-preview/pr-2/docs/extensions/infrastructure-extensions/gardener-extension-provider-aws/usage/#aws-workload-identity-federation">provider-aws</a></li><li><a href="/documentation/pr-preview/pr-2/docs/extensions/infrastructure-extensions/gardener-extension-provider-azure/usage/#azure-workload-identity-federation">provider-azure</a></li><li><a href="/documentation/pr-preview/pr-2/docs/extensions/infrastructure-extensions/gardener-extension-provider-gcp/usage/#gcp-workload-identity-federation">provider-gcp</a></li></ul>',5)]))}const f=o(d,[["render",i]]);export{u as __pageData,f as default};
